import React, { useState, useEffect } from 'react';
import { Box, Typography, Paper, Grid } from '@mui/material';
import { styled } from '@mui/material/styles';
import { subscribeToVendedoras } from '../services/vendedorasService';

const StyledPaper = styled(Paper)(({ theme }) => ({
  padding: theme.spacing(2),
  margin: theme.spacing(1),
  backgroundColor: 'rgba(0, 0, 0, 0.7)',
  color: 'white',
  borderRadius: '10px',
  transition: 'all 0.3s ease',
  '&:hover': {
    transform: 'scale(1.02)',
    boxShadow: '0 0 15px rgba(25, 118, 210, 0.5)',
  },
}));

const Dashboard = () => {
  const [salesData, setSalesData] = useState([]);

  useEffect(() => {
    const unsubscribe = subscribeToVendedoras((vendedoras) => {
      setSalesData(vendedoras);
    });

    return () => unsubscribe();
  }, []);

  return (
    <Box sx={{ 
      minHeight: '100vh', 
      bgcolor: 'background.default',
      p: 3,
      background: 'linear-gradient(45deg, #1a237e 30%, #0d47a1 90%)'
    }}>
      <Typography variant="h3" component="h1" gutterBottom align="center" sx={{ 
        color: 'white',
        textShadow: '2px 2px 4px rgba(0,0,0,0.5)',
        mb: 4
      }}>
        Top 10 Vendedoras
      </Typography>

      <Grid container spacing={2}>
        {salesData.map((vendedora, index) => (
          <Grid item xs={12} key={vendedora.id}>
            <StyledPaper elevation={3}>
              <Grid container alignItems="center" spacing={2}>
                <Grid item xs={1}>
                  <Typography variant="h4" color="primary">
                    #{index + 1}
                  </Typography>
                </Grid>
                <Grid item xs={7}>
                  <Typography variant="h5">
                    {vendedora.nome}
                  </Typography>
                </Grid>
                <Grid item xs={4}>
                  <Typography variant="h5" color="secondary" align="right">
                    R$ {Number(vendedora.valor).toLocaleString('pt-BR')}
                  </Typography>
                </Grid>
              </Grid>
            </StyledPaper>
          </Grid>
        ))}
      </Grid>
    </Box>
  );
};

export default Dashboard;
