import { 
  collection, 
  addDoc, 
  updateDoc, 
  deleteDoc, 
  doc, 
  onSnapshot,
  query,
  orderBy,
  limit
} from 'firebase/firestore';
import { db } from '../firebase';

const COLLECTION_NAME = 'vendedoras';

export const subscribeToVendedoras = (callback) => {
  const q = query(
    collection(db, COLLECTION_NAME),
    orderBy('valor', 'desc'),
    limit(10)
  );

  return onSnapshot(q, (snapshot) => {
    const vendedoras = [];
    snapshot.forEach((doc) => {
      vendedoras.push({ id: doc.id, ...doc.data() });
    });
    callback(vendedoras);
  });
};

export const addVendedora = async (vendedora) => {
  try {
    const docRef = await addDoc(collection(db, COLLECTION_NAME), {
      nome: vendedora.nome,
      valor: Number(vendedora.valor),
      createdAt: new Date()
    });
    return docRef.id;
  } catch (error) {
    console.error('Erro ao adicionar vendedora:', error);
    throw error;
  }
};

export const updateVendedora = async (id, vendedora) => {
  try {
    const docRef = doc(db, COLLECTION_NAME, id);
    await updateDoc(docRef, {
      nome: vendedora.nome,
      valor: Number(vendedora.valor),
      updatedAt: new Date()
    });
  } catch (error) {
    console.error('Erro ao atualizar vendedora:', error);
    throw error;
  }
};

export const deleteVendedora = async (id) => {
  try {
    const docRef = doc(db, COLLECTION_NAME, id);
    await deleteDoc(docRef);
  } catch (error) {
    console.error('Erro ao deletar vendedora:', error);
    throw error;
  }
};
